#!/usr/bin/env python

"""
DOC
"""


import os
import copy
import pickle

import numpy as np
import matplotlib as mpl


COLORS = {'dna': {
        'A': '#99cc33',
        'T': '#3366cc',
        'G': '#ff9900',
        'C': '#990000',
    }
}


def get_paths(font='Ubuntu Mono'):
    if font in _PATHS:
        obj = _PATHS[font]
    else:
        fn = os.path.join(os.path.dirname(__file__), font + '.pickle')
        with open(fn, 'rb') as f:
            obj = pickle.load(f)

        _PATHS[font] = obj

    return copy.deepcopy(obj)


__version__ = '0.0'


def logoplot(ax, seqmat, letters, height=None, color=None,
             x_offset=-0.5, y_offset=0.0):
    paths = get_paths()

    if color is None:
        color = {}
    elif color in COLORS:
        color = COLORS[color]

    if height is None:
        height = np.ones(len(seqmat))

    y_upper = []
    y_lower = []

    patches = []
    minsize = 1e-2

    for i in range(len(seqmat)):
        o_pos = 0
        o_neg = 0

        patches.append([])

        for j in np.argsort(np.abs(seqmat[i])):
            letter = letters[j]
            verts, codes = paths[letter]
            verts = verts.copy()
            
            verts[:, 0] = verts[:, 0] + i + x_offset

            h = seqmat[i, j] * height[i]
            if h < 0:
                h = -h
                o_neg += h
                verts[:, 1] = verts[:, 1] * h - o_neg + y_offset
            else:
                verts[:, 1] = verts[:, 1] * h + o_pos + y_offset
                o_pos += h
            
            if h > minsize:
                path = mpl.path.Path(verts, codes)
                c = color.get(letter, '#666666')
                patch = mpl.patches.PathPatch(path, facecolor=c, lw=0)
                ax.add_patch(patch)

                patches[-1].append(patch)

        y_upper.append(o_pos)
        y_lower.append(o_neg)

    ax.set_xlim(x_offset, len(seqmat) + x_offset)
    
    ylow, _ = ax.set_ylim(-np.max(y_lower) + y_offset, np.max(y_upper) + y_offset)

    return patches


